import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-list-mechanical-sheets',
  templateUrl: './list-mechanical-sheets.component.html',
  styleUrls: ['./list-mechanical-sheets.component.css']
})
export class ListMechanicalSheetsComponent implements OnInit {
  mechanicalSheets: any[] = [];
  mechanical_sheet_url = 'http://localhost:8081/api/FicheMeca'; // Mise à jour du nom de l'URL

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.loadMechanicalSheets();
  }

  loadMechanicalSheets() {
    const authToken = localStorage.getItem('authToken');

    if (!authToken) {
      console.error('No token found in localStorage');
      return;
    }

    const headers = new HttpHeaders({
      'Authorization': `Bearer ${authToken}`
    });

    this.http.get<any[]>(this.mechanical_sheet_url, { headers })
      .subscribe(response => {
        this.mechanicalSheets = response;
      }, error => {
        console.error('Error in GET request:', error);
      });
  }

  // Fonction pour obtenir les produits de l'assemblage
  getProducts(assemblage: any): string {
    const products = [];

    if (assemblage.moteurs.length > 0) {
      products.push('Moteur');
    }

    if (assemblage.pompes.length > 0) {
      products.push('Pompe');
    }

    if (assemblage.reducteurs.length > 0) {
      products.push('Réducteur');
    }

    if (assemblage.ventilateurs.length > 0) {
      products.push('Ventilateur');
    }

    return products.join(', ');
  }
}
<table class="table">
  <thead>
    <tr>
      <th scope="col">Numéro</th>
      <th scope="col">Client</th>
      <th scope="col">Date</th>
      <th scope="col">Produits</th>
      <th scope="col">Opérateur</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let sheet of mechanicalSheets" data-href="#">
      <th scope="row">{{ sheet.id }}</th>
      <td>{{ sheet.client.nom }}</td>
      <td>{{ sheet.dateCreation | date:'dd/MM/yyyy' }}</td>
      <td>{{ getProducts(sheet.assemblage) }}</td>
      <td>{{ sheet.modifiedBy }}</td>
    </tr>
  </tbody>
</table>
