// client.component.ts

import { Component, OnInit } from '@angular/core';
import { ShareDataService } from '../services/share-data-service.service';

export class Client {
  constructor(
    public client: string = '',
    public number: string = '',
    public name: string = '',
    public phone: string = '',
    public date: string = '',
    public ref: string = '',
    public mail: string = ''
  ) {}
}

@Component({
  selector: 'client',
  templateUrl: './client.component.html',
  styleUrls: ['./client.component.css']
})
export class ClientComponent implements OnInit {
  client: Client = new Client();

  constructor(private sharedDataService: ShareDataService) { }

  ngOnInit(): void {
    // Initialiser le numéro avec la date du jour et un numéro synchrone
    this.client.number = this.generateSyncNumber();
  }

  updateClientValue() {
    this.sharedDataService.updateClientValue(this.client);
  }

  generateSyncNumber(): string {
    // Logique pour générer le numéro synchrone
    const currentDate = new Date();
    const formattedDate = currentDate.toISOString().slice(0, 10).replace(/-/g, '');
    const syncNumber = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
    return formattedDate + syncNumber;
  }
}
