import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'client',
  templateUrl: './client.component.html',
  styleUrls: ['./client.component.css']
})
export class ClientComponent implements OnInit {
  numeroSerie: string = '';
  client: any = { client: '', name: '', phone: '', date: '', ref: '', mail: '' };

  ngOnInit(): void {
    this.genererNumeroSerie();
  }

  updateClientValue() {
    // Implémentez la logique nécessaire ici si nécessaire
  }

  genererNumeroSerie() {
    const dateActuelle = new Date();
    const annee = dateActuelle.getFullYear();
    const mois = (dateActuelle.getMonth() + 1).toString().padStart(2, '0');
    const jour = dateActuelle.getDate().toString().padStart(2, '0');

    let numeroIncrementalString = localStorage.getItem('numeroIncremental');
    let numeroIncremental = numeroIncrementalString ? parseInt(numeroIncrementalString) : 0;

    const cleDerniereMiseAJour = localStorage.getItem('derniereMiseAJour');
    if (!cleDerniereMiseAJour || cleDerniereMiseAJour !== jour) {
      numeroIncremental = 0;
    }
    localStorage.setItem('derniereMiseAJour', jour);
    numeroIncremental++;
    localStorage.setItem('numeroIncremental', numeroIncremental.toString());

    this.numeroSerie = `${annee}${mois}${jour}-${numeroIncremental}`;
  }
}
